-- Step 1: Extract and encode BLOB data from the source database\nDECLARE @BLOBData VARBINARY(MAX)\nSELECT @BLOBData = BLOBColumn FROM SourceTable WHERE YourCondition\n\nDECLARE @Base64Encoded NVARCHAR(MAX)\nSET @Base64Encoded = CAST('' AS XML).value('xs:base64Binary(sql:variable("@BLOBData"))', 'VARCHAR(MAX)')\n\n-- Step 2: Generate the SQL INSERT script\nDECLARE @InsertScript NVARCHAR(MAX)\nSET @InsertScript = 'INSERT INTO TargetTable (BLOBColumn) VALUES (CAST(''' + @Base64Encoded + ''' AS XML).value(''xs:base64Binary(.)'', ''VARBINARY(MAX)''))'\n\n-- Output the script to be run on the target database\nPRINT @InsertScript