SELECT \n    `orders`.*,\n    GROUP_CONCAT(\n        IF(\n            `menu_items`.`price` > 0,\n            CONCAT(\n                `menu_items`.`name`, \n                ' ($', \n                FORMAT(`menu_items`.`price`, 2), \n                ')'\n            ),\n            `menu_items`.`name`\n        ) SEPARATOR '<br>'\n    ) as `items`,\n    SUM(`menu_items`.`price`) as `additional`,\n    `children`.`first_name`,\n    `children`.`last_name`,\n    `organizations`.`base_price`\nFROM \n    `orders`\n    JOIN `order_items` ON `order_items`.`order_id` = `orders`.`id`\n    JOIN `menu_items` ON `order_items`.`menu_item_id` = `menu_items`.`id`\n    JOIN `children` ON `children`.`id` = `orders`.`child_id`\n    JOIN `organizations` ON `organizations`.`id` = `children`.`organization_id`\nWHERE \n    `orders`.`added_by` = {$user_id} AND\n    `orders`.`date` > '{$cutoff}'\nGROUP BY \n    `orders`.`id`;