-- Using Foreign Keys\nCREATE TABLE LookupTable (\n    id INT PRIMARY KEY,\n    value VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE MainTable (\n    id INT PRIMARY KEY,\n    lookup_id INT,\n    FOREIGN KEY (lookup_id) REFERENCES LookupTable(id),\n    other_column VARCHAR(255)\n);\n\n-- Storing Pure Data (not recommended)\nCREATE TABLE MainTableDirect (\n    id INT PRIMARY KEY,\n    lookup_value VARCHAR(255),\n    other_column VARCHAR(255)\n);