-- Insert into new tables based on status\nINSERT INTO messages_unread (message_id, sender_id, receiver_id, message_text, created_at)\nSELECT message_id, sender_id, receiver_id, message_text, created_at\nFROM messages\nWHERE status_id = 1;\n\nINSERT INTO messages_saved (message_id, sender_id, receiver_id, message_text, created_at)\nSELECT message_id, sender_id, receiver_id, message_text, created_at\nFROM messages\nWHERE status_id = 2;\n\nINSERT INTO messages_other (message_id, sender_id, receiver_id, message_text, created_at)\nSELECT message_id, sender_id, receiver_id, message_text, created_at\nFROM messages\nWHERE status_id IN (3, 4, 5);