-- Original messages table\nCREATE TABLE messages (\n    message_id INT PRIMARY KEY,\n    sender_id INT,\n    receiver_id INT,\n    message_text TEXT,\n    status_id INT,\n    created_at TIMESTAMP\n);\n\n-- Split into separate tables based on status\nCREATE TABLE messages_unread (\n    message_id INT PRIMARY KEY,\n    sender_id INT,\n    receiver_id INT,\n    message_text TEXT,\n    created_at TIMESTAMP\n);\n\nCREATE TABLE messages_saved (\n    message_id INT PRIMARY KEY,\n    sender_id INT,\n    receiver_id INT,\n    message_text TEXT,\n    created_at TIMESTAMP\n);\n\nCREATE TABLE messages_other (\n    message_id INT PRIMARY KEY,\n    sender_id INT,\n    receiver_id INT,\n    message_text TEXT,\n    created_at TIMESTAMP\n);