-- Original messages table\nCREATE TABLE messages (\n    message_id INT PRIMARY KEY,\n    sender_id INT,\n    receiver_id INT,\n    message_text TEXT,\n    status_id INT,\n    created_at TIMESTAMP\n);\n\n-- Split into user_messages and message_status\nCREATE TABLE user_messages (\n    message_id INT PRIMARY KEY,\n    sender_id INT,\n    receiver_id INT,\n    message_text TEXT,\n    created_at TIMESTAMP\n);\n\nCREATE TABLE message_status (\n    message_id INT PRIMARY KEY,\n    status_id INT\n);