DECLARE\n    v_sql VARCHAR2(32000);\nBEGIN\n    v_sql := 'CREATE OR REPLACE VIEW ALL_SERV AS ';\n    \n    FOR rec IN (SELECT username FROM all_users WHERE username LIKE 'DOMAIN%')\n    LOOP\n        v_sql := v_sql || 'SELECT * FROM ' || rec.username || '.SERV UNION ALL ';\n    END LOOP;\n    \n    -- Remove the last UNION ALL\n    v_sql := RTRIM(v_sql, 'UNION ALL ');\n\n    EXECUTE IMMEDIATE v_sql;\nEND;\n/