-- Add the linked server\nEXEC sp_addlinkedserver \n    @server = 'OracleLinkedServer', \n    @srvproduct = 'Oracle', \n    @provider = 'MSDAORA', \n    @datasrc = '10.1.3.42:1521/acc';\n\n-- Configure the login mapping\nEXEC sp_addlinkedsrvlogin \n    @rmtsrvname = 'OracleLinkedServer', \n    @useself = 'false', \n    @locallogin = NULL, \n    @rmtuser = 'UserName', \n    @rmtpassword = 'UserPassword';