-- Step 1: Add new column and populate new GUIDs\nALTER TABLE master_table ADD new_guid VARCHAR2(36);\nUPDATE master_table SET new_guid = YOUR_NEW_GUID_FUNCTION();\n\n-- Step 2: Update child tables\nALTER TABLE child_table ADD new_guid VARCHAR2(36);\nUPDATE child_table ct SET new_guid = (SELECT new_guid FROM master_table mt WHERE mt.old_guid = ct.old_guid);\n\n-- Step 3: Add new foreign key constraints\nALTER TABLE child_table ADD CONSTRAINT fk_new_guid FOREIGN KEY (new_guid) REFERENCES master_table(new_guid);\n\n-- Step 4: Drop old foreign key constraints\nALTER TABLE child_table DROP CONSTRAINT fk_old_guid;\n\n-- Step 5: Rename columns\nALTER TABLE master_table DROP COLUMN old_guid;\nALTER TABLE master_table RENAME COLUMN new_guid TO old_guid;\n\nALTER TABLE child_table DROP COLUMN old_guid;\nALTER TABLE child_table RENAME COLUMN new_guid TO old_guid;\n\n-- Step 6: Re-enable constraints if necessary\nALTER TABLE child_table ADD CONSTRAINT fk_old_guid FOREIGN KEY (old_guid) REFERENCES master_table(old_guid);