SELECT \n    Column1, \n    Column2, \n    Column3, \n    Column4, \n    Column5, \n    Column6, \n    Column7, \n    Column8, \n    Column9, \n    Column10,\n    COUNT(*) AS DuplicateCount\nFROM \n    YourTable\nGROUP BY \n    Column1, \n    Column2, \n    Column3, \n    Column4, \n    Column5, \n    Column6, \n    Column7, \n    Column8, \n    Column9, \n    Column10\nHAVING \n    COUNT(*) > 1\nORDER BY \n    DuplicateCount DESC;