SET ANSI_NULLS ON\nGO\nSET QUOTED_IDENTIFIER ON\nGO\n\nALTER VIEW [dbo].[viewProductExportUpdated2]\nAS\nWITH XmlData AS (\n    SELECT \n        dbo.ProductCategories.ProductCategoryID AS ProductCode, \n        dbo.ProductCategories.ProductCategoryName AS ProductTitle, \n        dbo.Products.ShortDescription AS ProductShortDesc, \n        dbo.Products.LongDescription AS ProductLongDesc,\n        CAST(dbo.Products.ProductXML AS XML) AS ProdXml\n    FROM \n        dbo.ProductCategories \n    INNER JOIN\n        dbo.Products ON dbo.ProductCategories.ProductCategoryID = dbo.Products.ProductCategoryID\n    WHERE \n        dbo.ProductCategories.Deleted = 0\n)\nSELECT\n    ProductCode,\n    ProductTitle,\n    ProductShortDesc,\n    ProductLongDesc,\n    ProdXml,\n    ProdXml.value('data(//Products/PackSize)[1]', 'nvarchar(max)') AS ProductPackSize\nFROM \n    XmlData\nGO\n\nSET ANSI_NULLS OFF\nGO\nSET QUOTED_IDENTIFIER OFF\nGO