-- Create a new table to store the data from Excel\nCREATE TABLE ExcelData (\n    Column1 NVARCHAR(255),\n    Column2 NVARCHAR(255),\n    Column3 NVARCHAR(255)\n    -- Add additional columns as required\n);\n\n-- Use OPENROWSET to import data from Excel\nINSERT INTO ExcelData (Column1, Column2, Column3)\nSELECT *\nFROM OPENROWSET('Microsoft.ACE.OLEDB.12.0',\n    'Excel 12.0;Database=C:\Path\To\Your\ExcelFile.xlsx;HDR=YES',\n    'SELECT * FROM [Sheet1$]');