CREATE TABLE NewMilestones (\n    MilestoneID UNIQUEIDENTIFIER PRIMARY KEY,\n    Name NVARCHAR(255) NOT NULL\n);\n\nCREATE TABLE NewCategories (\n    CategoryID UNIQUEIDENTIFIER PRIMARY KEY,\n    Name NVARCHAR(255) NOT NULL\n);\n\nCREATE TABLE NewSkills (\n    SkillID UNIQUEIDENTIFIER PRIMARY KEY,\n    Name NVARCHAR(255) NOT NULL\n);\n\nCREATE TABLE NewPayRates (\n    RateID UNIQUEIDENTIFIER PRIMARY KEY,\n    Name NVARCHAR(255) NOT NULL\n);\n\nCREATE TABLE NewTasks (\n    TaskID UNIQUEIDENTIFIER PRIMARY KEY,\n    TaskName NVARCHAR(255) NOT NULL,\n    MilestoneID UNIQUEIDENTIFIER,\n    CategoryID UNIQUEIDENTIFIER,\n    SkillID UNIQUEIDENTIFIER,\n    RateID UNIQUEIDENTIFIER,\n    FOREIGN KEY (MilestoneID) REFERENCES NewMilestones(MilestoneID),\n    FOREIGN KEY (CategoryID) REFERENCES NewCategories(CategoryID),\n    FOREIGN KEY (SkillID) REFERENCES NewSkills(SkillID),\n    FOREIGN KEY (RateID) REFERENCES NewPayRates(RateID)\n);