INSERT INTO NewTasks (TaskID, TaskName, MilestoneID, CategoryID, SkillID, RateID)\nSELECT \n    t.TaskID,\n    t.TaskName,\n    m.MilestoneID,\n    c.CategoryID,\n    s.SkillID,\n    p.RateID\nFROM \n    Tasks t\nJOIN \n    Milestones m ON t.Milestone = m.Name\nJOIN \n    Categories c ON t.Category = c.Name\nJOIN \n    Skills s ON t.Skill = s.Name\nJOIN \n    PayRates p ON t.Rate = p.Name;