DECLARE @DatabaseName NVARCHAR(255)\nDECLARE @SQL NVARCHAR(MAX)\n\n-- Table to store results\nCREATE TABLE #Results (DatabaseName NVARCHAR(255))\n\nDECLARE db_cursor CURSOR FOR\nSELECT name\nFROM sys.databases\nWHERE state_desc = 'ONLINE' -- Only consider online databases\n\nOPEN db_cursor\nFETCH NEXT FROM db_cursor INTO @DatabaseName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Construct the query to check if the table exists in the current database\n    SET @SQL = 'IF EXISTS (SELECT 1 FROM ' + QUOTENAME(@DatabaseName) + '.sys.tables WHERE name = ''MyTable'')\n                BEGIN\n                    INSERT INTO #Results (DatabaseName) VALUES (''' + @DatabaseName + ''')\n                END'\n\n    -- Execute the query\n    EXEC sp_executesql @SQL\n\n    FETCH NEXT FROM db_cursor INTO @DatabaseName\nEND\n\nCLOSE db_cursor\nDEALLOCATE db_cursor\n\n-- Select results\nSELECT * FROM #Results\n\n-- Clean up\nDROP TABLE #Results