-- Using VARCHAR2 in Oracle\nCREATE TABLE example (\n    id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,\n    name VARCHAR2(255)\n);\n\n-- Using NVARCHAR2 for Unicode in Oracle\nCREATE TABLE example_unicode (\n    id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,\n    description NVARCHAR2(5000)\n);