SELECT\n    a.constraint_name,\n    a.column_name,\n    b.constraint_name AS referenced_constraint_name,\n    b.table_name AS referenced_table_name,\n    b.column_name AS referenced_column_name\nFROM\n    user_cons_columns a\nJOIN\n    user_constraints c ON a.constraint_name = c.constraint_name\nJOIN\n    user_cons_columns b ON c.r_constraint_name = b.constraint_name\nWHERE\n    c.constraint_type = 'R'\n    AND a.table_name = 'ARTIST';