SELECT\n  c.name AS category,\n  GROUP_CONCAT(DISTINCT sc.name ORDER BY sc.name SEPARATOR ' > ') AS subcategory,\n  GROUP_CONCAT(DISTINCT CONCAT(sc.name, ' > ', ssc.name) ORDER BY sc.name, ssc.name SEPARATOR ' > ') AS subsubcategory\nFROM\n  categories c\nLEFT JOIN\n  subcategories sc\n  ON c.id = sc.category_id\nLEFT JOIN\n  subsubcategories ssc\n  ON sc.id = ssc.subcategory_id\nGROUP BY\n  c.name WITH ROLLUP\nHAVING\n  category IS NOT NULL;