SELECT ti.itemId, ti.title, tis.statusKey, tis.statusDate\nFROM todo_item ti\nJOIN todo_project tp ON ti.projectId = tp.projectId\nJOIN (\n    SELECT itemId, MAX(statusDate) AS latestDate\n    FROM todo_itemStatus\n    GROUP BY itemId\n) latestStatus ON ti.itemId = latestStatus.itemId\nJOIN todo_itemStatus tis ON latestStatus.itemId = tis.itemId AND latestStatus.latestDate = tis.statusDate\nWHERE tp.projectId = 13\nORDER BY ti.itemId;