-- Create Linked Server\nEXEC sp_addlinkedserver \n    @server = 'SourceServer', \n    @srvproduct = '', \n    @provider = 'SQLNCLI', \n    @datasrc = 'SourceServer';\n\nEXEC sp_addlinkedsrvlogin \n    @rmtsrvname = 'SourceServer', \n    @useself = 'false', \n    @rmtuser = 'sourceUser', \n    @rmtpassword = 'sourcePassword';\n\n-- Insert Data\nINSERT INTO DestinationDB.dbo.TargetTable (ID, Name, Value)\nSELECT ID, Name, Value\nFROM SourceServer.SourceDB.dbo.SourceTable\nWHERE Rule1 = 1;