SELECT \n    KU.TABLE_NAME,\n    KU.COLUMN_NAME\nFROM \n    INFORMATION_SCHEMA.TABLE_CONSTRAINTS AS TC\n    INNER JOIN INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS KU\n        ON TC.CONSTRAINT_TYPE = 'PRIMARY KEY' \n        AND TC.CONSTRAINT_NAME = KU.CONSTRAINT_NAME\nWHERE \n    KU.TABLE_NAME = 'YourTableName';