-- Replace 'LinkedServerName' with the name of your linked server\n-- Replace 'DatabaseName' with the name of the database on the linked server\n-- Replace 'SchemaName.TableName' with the schema and table name on the linked server\n\nDECLARE @sql NVARCHAR(MAX) = N'\nINSERT INTO SchemaName.TableName (Column1, Column2, Column3)\nVALUES (Value1, Value2, Value3);';\n\nEXEC LinkedServerName.DatabaseName.sys.sp_executesql @sql;