DECLARE \n    objName VARCHAR2(50);\n    FileName VARCHAR2(100);\n    fileHandler UTL_FILE.FILE_TYPE;\n    ddl_statement CLOB;\n\n    CURSOR curProcs IS\n        SELECT OBJECT_NAME \n        FROM ALL_PROCEDURES \n        WHERE OWNER = 'AMS' \n        ORDER BY OBJECT_NAME;\nBEGIN\n    OPEN curProcs;\n    LOOP\n        FETCH curProcs INTO objName;\n        EXIT WHEN curProcs%NOTFOUND;\n\n        FileName := 'C:\your\path\' || objName || '.PRC.SQL';\n        \n        -- Get the DDL statement\n        ddl_statement := DBMS_METADATA.GET_DDL('PROCEDURE', objName, 'AMS');\n\n        -- Open the file for writing\n        fileHandler := UTL_FILE.FOPEN('YOUR_DIRECTORY_ALIAS', FileName, 'W');\n\n        -- Write the DDL statement to the file\n        UTL_FILE.PUT_LINE(fileHandler, ddl_statement);\n\n        -- Close the file\n        UTL_FILE.FCLOSE(fileHandler);\n    END LOOP;\n    CLOSE curProcs;\nEND;\n/