CREATE TRIGGER BEFORE_PROJECT_DELETE\nBEFORE DELETE ON PROJECTS\nFOR EACH ROW\nAS\nBEGIN\n    -- Delete entries from DEPENDENCIES where the first file belongs to the project being deleted\n    DELETE FROM DEPENDENCIES\n    WHERE DEPENDENCIES.FILE1_ID IN (SELECT FILE_ID FROM FILES WHERE PROJECT_ID = OLD.PROJECT_ID);\n\n    -- Delete entries from DEPENDENCIES where the second file belongs to the project being deleted\n    DELETE FROM DEPENDENCIES\n    WHERE DEPENDENCIES.FILE2_ID IN (SELECT FILE_ID FROM FILES WHERE PROJECT_ID = OLD.PROJECT_ID);\n\n    -- Delete all files belonging to the project\n    DELETE FROM FILES WHERE PROJECT_ID = OLD.PROJECT_ID;\nEND