-- Create the temp table 'grabname' with the appropriate structure\nSELECT DISTINCT reqhead_rec.resp_name\nFROM reqhead_rec, biglist\nWHERE reqhead_rec.req_no = biglist.req_no\n AND reqhead_rec.frm = biglist.req_frm\nINTO TEMP grabname with no log;\n\n-- Insert a dummy row if 'grabname' is empty\nINSERT INTO grabname (resp_name)\nSELECT '' \nWHERE NOT EXISTS (SELECT 1 FROM grabname);\n\n-- Select from 'biglist' and 'grabname', adding a dummy column to 'biglist' to match the structure\nSELECT biglist.*, grabname.resp_name\nFROM biglist\nLEFT JOIN grabname ON 1 = 1  -- Cross join to ensure all rows\nORDER BY prnt_item, account_amt\nINTO TEMP xxx with no log;