-- Example of batching:\n   DECLARE @BatchSize INT = 10000;\n   DECLARE @BatchStart INT = 0;\n   WHILE (1=1)\n   BEGIN\n       BEGIN TRANSACTION;\n       UPDATE TOP (@BatchSize) YourTable\n       SET NewColumn = SomeValue\n       WHERE SomeCondition AND ID > @BatchStart;\n       \n       IF @@ROWCOUNT = 0 BREAK;\n       \n       SET @BatchStart = @BatchStart + @BatchSize;\n       COMMIT TRANSACTION;\n   END