-- Create an index on the id_usr column in users_usr table\nCREATE INDEX idx_users_usr_id_usr ON users_usr (id_usr);\n\n-- Create an index on the uid_usr column in credit_acc table\nCREATE INDEX idx_credit_acc_uid_usr ON credit_acc (uid_usr);\n\n-- Create an index on the id_cfc column in cfc_cfc table\nCREATE INDEX idx_cfc_cfc_id_cfc ON cfc_cfc (id_cfc);\n\n-- Create a composite index on the type_acc, status_acc, and linetype_acc columns in credit_acc table\nCREATE INDEX idx_credit_acc_type_status_linetype ON credit_acc (type_acc, status_acc, linetype_acc);\n\n-- Create an index on the credit_used_acc column in credit_acc table\nCREATE INDEX idx_credit_acc_credit_used_acc ON credit_acc (credit_used_acc);