SELECT\n    i.id,\n    i.title,\n    g.id AS groupId,\n    g.groupTitle,\n    g.externalURL\nFROM\n    items i\nLEFT JOIN\n    groups g ON i.groupId = g.id\n\nUNION\n\nSELECT\n    NULL AS id,\n    NULL AS title,\n    g.id AS groupId,\n    g.groupTitle,\n    g.externalURL\nFROM\n    groups g\nWHERE\n    g.externalURL IS NOT NULL\n    AND g.id NOT IN (SELECT DISTINCT groupId FROM items);