CREATE TABLE proximity (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    business_id INT,\n    reference_location_id INT,\n    distance FLOAT,\n    INDEX (business_id),\n    INDEX (reference_location_id)\n);\n\n-- Assume you have a reference_locations table with fixed points\nINSERT INTO proximity (business_id, reference_location_id, distance)\nSELECT b.id, r.id, ( 3959 * acos( cos( radians(r.latitude) ) * cos( radians(b.latitude) ) * cos( radians(b.longitude) - radians(r.longitude) ) + sin( radians(r.latitude) ) * sin( radians(b.latitude) ) ) ) AS distance\nFROM businesses b\nCROSS JOIN reference_locations r;