DECLARE @PartitionFunctionName NVARCHAR(128) = 'YourPartitionFunctionName';\n\nSELECT \n    pf.name AS PartitionFunctionName,\n    r.boundary_id AS BoundaryID,\n    v.value AS BoundaryValue\nFROM \n    sys.partition_functions pf\n    JOIN sys.partition_range_values v ON pf.function_id = v.function_id\n    JOIN sys.partition_schemes ps ON pf.function_id = ps.function_id\n    JOIN sys.destination_data_spaces ds ON ps.data_space_id = ds.partition_scheme_id\n    JOIN sys.partition_boundaries r ON ds.destination_id = r.boundary_id\nWHERE \n    pf.name = @PartitionFunctionName\nORDER BY \n    r.boundary_id;