-- Create a sequence for the order number\nCREATE SEQUENCE OrderNumberSequence\n    START WITH 1\n    INCREMENT BY 1;\n\n-- Create the table with primary key and order number columns\nCREATE TABLE Orders (\n    OrderID INT IDENTITY(1,1) PRIMARY KEY,\n    OrderNumber INT,\n    OrderDescription NVARCHAR(100)\n);\n\n-- Create a trigger to set the order number\nCREATE TRIGGER SetOrderNumber\nON Orders\nAFTER INSERT\nAS\nBEGIN\n    UPDATE Orders\n    SET OrderNumber = NEXT VALUE FOR OrderNumberSequence\n    FROM Orders\n    INNER JOIN inserted\n    ON Orders.OrderID = inserted.OrderID;\nEND;