ALTER TABLE YourTable ADD NewColumn VARCHAR(50);\n   GO\n\n   -- Update the new column in batches\n   DECLARE @BatchSize INT = 10000;\n   DECLARE @MinID INT = (SELECT MIN(ID) FROM YourTable);\n   DECLARE @MaxID INT = (SELECT MAX(ID) FROM YourTable);\n   DECLARE @CurrentID INT = @MinID;\n   \n   WHILE @CurrentID <= @MaxID\n   BEGIN\n       UPDATE TOP (@BatchSize) YourTable\n       SET NewColumn = 'DefaultValue'\n       WHERE ID BETWEEN @CurrentID AND @CurrentID + @BatchSize - 1;\n       \n       SET @CurrentID = @CurrentID + @BatchSize;\n   END\n   GO\n   \n   -- Add default constraint\n   ALTER TABLE YourTable\n   ADD CONSTRAINT DF_YourTable_NewColumn DEFAULT 'DefaultValue' FOR NewColumn;\n   GO