-- Create new table with desired schema\n   CREATE TABLE NewYourTable (\n       ID INT PRIMARY KEY,\n       Column1 VARCHAR(50),\n       Column2 INT,\n       -- Other columns\n       NewColumn VARCHAR(50) DEFAULT 'DefaultValue'\n   );\n   GO\n\n   -- Copy data in batches\n   DECLARE @BatchSize INT = 10000;\n   DECLARE @MinID INT = (SELECT MIN(ID) FROM YourTable);\n   DECLARE @MaxID INT = (SELECT MAX(ID) FROM YourTable);\n   DECLARE @CurrentID INT = @MinID;\n\n   WHILE @CurrentID <= @MaxID\n   BEGIN\n       INSERT INTO NewYourTable (ID, Column1, Column2, -- Other columns, NewColumn)\n       SELECT ID, Column1, Column2, -- Other columns, 'DefaultValue'\n       FROM YourTable\n       WHERE ID BETWEEN @CurrentID AND @CurrentID + @BatchSize - 1;\n       \n       SET @CurrentID = @CurrentID + @BatchSize;\n   END\n   GO\n\n   -- Rename tables\n   EXEC sp_rename 'YourTable', 'OldYourTable';\n   EXEC sp_rename 'NewYourTable', 'YourTable';\n   GO