SELECT \n    DF.FIELDID, \n    DF.FIELDNAME, \n    DF.NAME AS FIELDLABEL, \n    DT.NAME AS TABLENAME \nFROM \n    SQLDICTIONARYFIELD DF\nJOIN \n    SQLDICTIONARY DT ON DF.TABLEID = DT.TABLEID\nWHERE \n    DT.NAME = 'YourTableName'\n    AND DF.FIELDNAME LIKE 'YourCustomFieldPrefix%' -- Adjust the prefix as needed\nORDER BY \n    DF.FIELDID;