WITH ImageRanks AS (\n    SELECT *,\n        CASE IMAGENAME\n            WHEN 'IMG1' THEN 1\n            WHEN 'IMG2' THEN 2\n            WHEN 'IMG3' THEN 3\n            WHEN 'IMG4' THEN 4\n            WHEN 'IMG5' THEN 5\n            WHEN 'IMG6' THEN 6\n            ELSE 7\n        END AS Rank\n    FROM IMAGES\n    WHERE IMAGENAME IN ('IMG1', 'IMG2', 'IMG3', 'IMG4', 'IMG5', 'IMG6')\n)\nSELECT *\nFROM ImageRanks\nORDER BY Rank;