USE [master]\nGO\n\n-- Add a new job for log backups\nEXEC msdb.dbo.sp_add_job @job_name=N'Log Shipping Backup Job', \n        @enabled=1, \n        @notify_level_eventlog=0, \n        @notify_level_email=0, \n        @notify_level_netsend=0, \n        @notify_level_page=0, \n        @delete_level=0, \n        @description=N'Log shipping backup job for primary database', \n        @category_name=N'[Uncategorized (Local)]', \n        @owner_login_name=N'sa';\n\n-- Add a job step\nEXEC msdb.dbo.sp_add_jobstep @job_name=N'Log Shipping Backup Job', \n        @step_name=N'Backup Log', \n        @step_id=1, \n        @subsystem=N'TSQL', \n        @command=N'BACKUP LOG [YourDatabase] TO DISK = ''\\YourBackupPath\YourDatabase.trn'' WITH INIT, COMPRESSION', \n        @database_name=N'master', \n        @flags=0;\n\n-- Set the job schedule\nEXEC msdb.dbo.sp_add_jobschedule @job_name=N'Log Shipping Backup Job', \n        @name=N'Log Shipping Backup Schedule', \n        @enabled=1, \n        @freq_type=4, \n        @freq_interval=1, \n        @freq_subday_type=4, \n        @freq_subday_interval=15, \n        @freq_relative_interval=0, \n        @freq_recurrence_factor=0, \n        @active_start_date=20220101, \n        @active_start_time=233000, \n        @active_end_date=99991231, \n        @active_end_time=235900;\n\n-- Add the job to the SQL Server Agent\nEXEC msdb.dbo.sp_add_jobserver @job_name=N'Log Shipping Backup Job', @server_name = N'(local)';\nGO