SELECT ClaimNumber, BillReceivedDate, DocTrackNumber\nFROM (\n    SELECT DISTINCT c.CN as ClaimNumber, \n           a.ItemDate as BillReceivedDate, \n           c.DTN as DocTrackNumber,\n           a.DateStored\n    FROM ItemData a,\n         ItemDataPage b,\n         KeyGroupData c\n    WHERE a.ItemTypeNum in (112, 113, 116, 172, 189)\n      AND a.ItemNum = b.ItemNum\n      AND b.ItemNum = c.ItemNum\n)\nORDER BY DateStored DESC;