SELECT \n    m.PersonName,\n    c.CourseName,\n    d.StartDate,\n    d.EndDate\nFROM \n    MasterTable m\nJOIN \n    DetailTable d ON m.ID = d.MasterID\nJOIN \n    CourseTable c ON d.CourseID = c.ID\nWHERE \n    (d.MasterID, d.StartDate) IN (\n        SELECT \n            MasterID, MAX(StartDate)\n        FROM \n            DetailTable\n        GROUP BY \n            MasterID\n    )