DECLARE\n       l_encrypted_blob BLOB;\n       l_decrypted_blob BLOB;\n   BEGIN\n       -- Encrypt\n       DBMS_CRYPTO.ENCRYPT(\n           src => data,\n           typ => DBMS_CRYPTO.DES_CBC_PKCS5,\n           key => 'my_secret_key'\n       );\n\n       -- Decrypt\n       DBMS_CRYPTO.DECRYPT(\n           src => l_encrypted_blob,\n           typ => DBMS_CRYPTO.DES_CBC_PKCS5,\n           key => 'my_secret_key',\n           dst => l_decrypted_blob\n       );\n   END;