WITH RecursiveCTE AS (\n    -- Anchor member: start with James\n    SELECT CustomerID, ParentID, Name\n    FROM Customers\n    WHERE CustomerID = 2\n\n    UNION ALL\n\n    -- Recursive member: get all descendants\n    SELECT c.CustomerID, c.ParentID, c.Name\n    FROM Customers c\n    INNER JOIN RecursiveCTE r ON c.ParentID = r.CustomerID\n)\nSELECT CustomerID, ParentID, Name\nFROM RecursiveCTE;