SELECT \n    COALESCE(ContactDate, 'No Entry') AS ContactDate,\n    COALESCE(SortName, 'No Entry') AS SortName,\n    COALESCE(City, 'No Entry') AS City,\n    COALESCE(ContactType, 'No Entry') AS ContactType,\n    COALESCE(Summary, 'No Entry') AS Summary\nFROM \n    ContactTable ct\nJOIN \n    UserTable ut ON ct.UserID = ut.UserID\nJOIN \n    AddressTable at ON ut.AddressID = at.AddressID;