CREATE TABLE Students (\n    StudentID INT PRIMARY KEY AUTO_INCREMENT,\n    StudentName VARCHAR(100) NOT NULL\n);\n\nCREATE TABLE Subjects (\n    SubjectID INT PRIMARY KEY AUTO_INCREMENT,\n    SubjectName VARCHAR(100) NOT NULL\n);\n\nCREATE TABLE Periods (\n    PeriodID INT PRIMARY KEY AUTO_INCREMENT,\n    PeriodName VARCHAR(50) NOT NULL\n);\n\nCREATE TABLE Grades (\n    GradeID INT PRIMARY KEY AUTO_INCREMENT,\n    StudentID INT,\n    SubjectID INT,\n    PeriodID INT,\n    AverageGrade DECIMAL(4,2),\n    MissedClasses INT,\n    RecoveringGrade DECIMAL(4,2),\n    FOREIGN KEY (StudentID) REFERENCES Students(StudentID),\n    FOREIGN KEY (SubjectID) REFERENCES Subjects(SubjectID),\n    FOREIGN KEY (PeriodID) REFERENCES Periods(PeriodID)\n);\n\nCREATE TABLE YearlyGrades (\n    YearlyGradeID INT PRIMARY KEY AUTO_INCREMENT,\n    StudentID INT,\n    SubjectID INT,\n    YearAverage DECIMAL(4,2),\n    FinalRecoveringGrade DECIMAL(4,2),\n    FOREIGN KEY (StudentID) REFERENCES Students(StudentID),\n    FOREIGN KEY (SubjectID) REFERENCES Subjects(SubjectID)\n);