CREATE PROCEDURE InsertArrayOfValues\n    @Data XML\nAS\nBEGIN\n    -- Insert the data into the table\n    INSERT INTO MyTable (ID, FieldName1, FieldName2)\n    SELECT \n        T.item.value('(ID)[1]', 'INT') AS ID,\n        T.item.value('(FieldName1)[1]', 'VARCHAR(100)') AS FieldName1,\n        T.item.value('(FieldName2)[1]', 'INT') AS FieldName2\n    FROM \n        @Data.nodes('/Root/Row') T(item);\nEND