CREATE PROC Insert_Product \n(\n    @ProductID uniqueidentifier, \n    @IDProductType uniqueidentifier, \n    @ProductName nvarchar(255), \n    @ProductCode nvarchar(50), \n    @Weight decimal(18,4), \n    @Volume decimal(18,4)\n) \nAS \nBEGIN\n    INSERT INTO tblProduct \n    (ProductID, IDProductType, ProductName, ProductCode, Weight, Volume) \n    VALUES \n    (@ProductID, @IDProductType, @ProductName, @ProductCode, @Weight, @Volume)\nEND