-- Example: Converting a cursor update to a set-based update\n\n-- Original cursor-based approach\nDECLARE @id INT\nDECLARE cursor_example CURSOR FOR\nSELECT id FROM your_table\n\nOPEN cursor_example\nFETCH NEXT FROM cursor_example INTO @id\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Do your update operation here\n    UPDATE your_table\n    SET column = value\n    WHERE id = @id\n    \n    FETCH NEXT FROM cursor_example INTO @id\nEND\n\nCLOSE cursor_example\nDEALLOCATE cursor_example\n\n-- Set-based approach\nUPDATE your_table\nSET column = value\nWHERE some_conditio