CREATE PROC Update_Product\n(\n    @ProductID uniqueidentifier,\n    @IDProductType uniqueidentifier = NULL,\n    @ProductName nvarchar(255) = NULL,\n    @ProductCode nvarchar(50) = NULL,\n    @Weight decimal(18,4) = NULL,\n    @Volume decimal(18,4) = NULL\n)\nAS \nBEGIN\n    UPDATE tblProduct\n    SET \n        IDProductType = COALESCE(@IDProductType, IDProductType),\n        ProductName = COALESCE(@ProductName, ProductName),\n        ProductCode = COALESCE(@ProductCode, ProductCode),\n        Weight = COALESCE(@Weight, Weight),\n        Volume = COALESCE(@Volume, Volume)\n    WHERE \n        ProductID = @ProductID\nEND