-- Variables for new data\nDECLARE @Id INT = 1; -- Example ID\nDECLARE @Value NVARCHAR(50) = 'NewValue'; -- Example value\n\n-- First, try to update the existing row\nUPDATE YourTable\nSET ValueColumn = @Value\nWHERE Id = @Id;\n\n-- Check the number of rows affected by the update\nIF @@ROWCOUNT = 0\nBEGIN\n    -- If no rows were affected, insert the new row\n    INSERT INTO YourTable (Id, ValueColumn)\n    VALUES (@Id, @Value);\nEND;