DECLARE\n    cols VARCHAR2(4000);\nBEGIN\n    -- Get the column names from Schema1.tableA\n    SELECT LISTAGG(column_name, ', ') WITHIN GROUP (ORDER BY column_id)\n    INTO cols\n    FROM all_tab_columns\n    WHERE owner = 'SCHEMA1' AND table_name = 'TABLEA';\n\n    -- Construct and execute the dynamic SQL\n    EXECUTE IMMEDIATE 'INSERT INTO Schema1.tableA (' || cols || ') SELECT ' || cols || ' FROM Schema2.tableA';\nEND;\n/