-- Example of creating a table with an IDENTITY column as the clustered index\nCREATE TABLE ExampleTable (\n    Id INT IDENTITY(1,1) PRIMARY KEY CLUSTERED,\n    GuidColumn UNIQUEIDENTIFIER DEFAULT NEWID() NONCLUSTERED,\n    -- other columns\n);\n\n-- Example of using NEWSEQUENTIALID() for generating sequential GUIDs\nCREATE TABLE ExampleTableSequential (\n    Id UNIQUEIDENTIFIER DEFAULT NEWSEQUENTIALID() PRIMARY KEY CLUSTERED,\n    -- other columns\n);