-- Create a new login for the DBA\nCREATE LOGIN dba_login WITH PASSWORD = 'secure_password';\n\n-- Create a new user in the specific database\nUSE your_database;\nCREATE USER dba_user FOR LOGIN dba_login;\n\n-- Grant read-only access to the specific tables\nGRANT SELECT ON dbo.your_table TO dba_user;\n\n-- Optionally, create a role for minimal privileges\nCREATE ROLE read_only_role;\nALTER ROLE read_only_role ADD MEMBER dba_user;